// ------------------------------------
// NEW: Authentication Service using Firestore and SharedPreferences
// ------------------------------------
class AuthService {
  final StreamController<bool> _authStateController =
      StreamController<bool>.broadcast();
  Stream<bool> get authStateChanges => _authStateController.stream;

  final FirebaseFirestore _firestore = FirebaseFirestore.instance;
  final String _collectionName = 'users'; // Firestore collection name
  final String _prefsKey = 'isLoggedIn'; // SharedPreferences key

  AuthService() {
    _checkInitialLoginStatus();
  }

  void _checkInitialLoginStatus() async {
    final prefs = await SharedPreferences.getInstance();
    final isLoggedIn = prefs.getBool(_prefsKey) ?? false;
    _authStateController.add(isLoggedIn);
  }

  Future<void> signUp(String email, String password) async {
    // 1. Check if user already exists
    final result = await _firestore
        .collection(_collectionName)
        .doc(email)
        .get();
    if (result.exists) {
      throw 'Account already exists for this email.';
    }

    // 2. Create the account (only store email and password placeholder)
    // NOTE: In production, password_hash should use proper hashing (e.g., bcrypt)
    await _firestore.collection(_collectionName).doc(email).set({
      'email': email,
      'password_hash': password.hashCode, // Mock password hash
      'created_at': FieldValue.serverTimestamp(),
    });

    // 3. Log the user in after successful registration
    await _storeLoginState(true, email);
    _authStateController.add(true);
  }

  Future<void> signIn(String email, String password) async {
    // 1. Check credentials (Mock validation against stored hash)
    final userDoc = await _firestore
        .collection(_collectionName)
        .doc(email)
        .get();

    if (!userDoc.exists || userDoc['password_hash'] != password.hashCode) {
      throw 'Invalid email or password.';
    }

    // 2. Store login state and transition
    await _storeLoginState(true, email);
    _authStateController.add(true);
  }

  Future<void> signOut() async {
    await _storeLoginState(false, null);
    _authStateController.add(false);
  }

  Future<void> _storeLoginState(bool isLoggedIn, String? email) async {
    final prefs = await SharedPreferences.getInstance();
    await prefs.setBool(_prefsKey, isLoggedIn);
    if (email != null) {
      await prefs.setString('userEmail', email); // Save email for autofill
    } else {
      await prefs.remove('userEmail');
    }
  }

  Future<String?> getLastEmail() async {
    final prefs = await SharedPreferences.getInstance();
    return prefs.getString('userEmail');
  }

  void dispose() {
    _authStateController.close();
  }
}
// ------------------------------------

final AuthService _authService = AuthService();

void main() async {
  WidgetsFlutterBinding.ensureInitialized();

  // FIX 1: Initialization is outside the declaration block and uses the generated options
  await Firebase.initializeApp(options: DefaultFirebaseOptions.currentPlatform);

  runApp(const WeatherApp());
}

class WeatherApp extends StatelessWidget {
  const WeatherApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Calm Weather', // Renamed app title
      debugShowCheckedModeBanner: false,
      theme: ThemeData(
        primarySwatch: Colors.blueGrey,
        fontFamily: 'Inter',
        visualDensity: VisualDensity.adaptivePlatformDensity,
      ),
      home: StreamBuilder<bool>(
        initialData: false,
        stream: _authService.authStateChanges,
        builder: (context, snapshot) {
          if (snapshot.connectionState == ConnectionState.active ||
              snapshot.hasData) {
            final isLoggedIn = snapshot.data!;
            if (isLoggedIn) {
              return const WeatherScreen();
            } else {
              // The AuthScreen manages the switch between Login and Sign Up tabs
              return const AuthScreen();
            }
          }
          // Initial loading screen while checking auth state
          return const Scaffold(
            body: Center(child: CircularProgressIndicator()),
            backgroundColor: Color(0xFF88A9C3),
          );
        },
      ),
    );
  }
}

