// NEW: Login Screen Component
// ------------------------------------
class LoginScreen extends StatefulWidget {
  const LoginScreen({super.key});

  @override
  State<LoginScreen> createState() => _LoginScreenState();
}

class _LoginScreenState extends State<LoginScreen> {
  // FIX 2: Controllers are now defined in the State class
  final TextEditingController _emailController = TextEditingController();
  final TextEditingController _passwordController = TextEditingController();

  // NEW: State for password visibility toggle
  bool _isPasswordVisible = false;

  bool _isLoading = false;
  String? _error;

  static const Color _calmAccentTeal = Color(0xFF5B9B9D);
  static const Color _calmDarkBlue = Color(0xFF4C7B8F);
  static const Color _calmPrimaryTextColor = Color(0xFF333D47);

  @override
  void initState() {
    super.initState();
    _loadLastEmail();
  }

  void _loadLastEmail() async {
    final email = await _authService.getLastEmail();
    if (email != null && mounted) {
      _emailController.text = email;
    }
  }

  Future<void> _login() async {
    setState(() {
      _isLoading = true;
      _error = null;
    });

    try {
      await _authService.signIn(
        _emailController.text,
        _passwordController.text,
      );
    } catch (e) {
      if (mounted) {
        setState(() {
          _error = e.toString();
          _isLoading = false;
        });
      }
    }
  }

  // Helper for forgotten password flow (3-step modal)
  void _showForgotPasswordFlow(BuildContext context) {
    String stepError = '';
    // Controllers for the modal steps are defined locally inside the function
    TextEditingController emailResetController = TextEditingController(
      text: _emailController.text,
    );
    TextEditingController codeController = TextEditingController();
    TextEditingController newPasswordController = TextEditingController();

    // Simulates sending a code to the email
    void sendCode() {
      if (!emailResetController.text.endsWith('@gmail.com')) {
        ScaffoldMessenger.of(context).showSnackBar(
          const SnackBar(
            content: Text('Error: Email must end with @gmail.com.'),
          ),
        );
      } else {
        Navigator.of(context).pop();
        _showCodeInput(
          context,
          emailResetController.text,
          codeController,
          newPasswordController,
        );
      }
    }

    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text("Reset Password (Step 1/3)"),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: <Widget>[
              const Text(
                "Enter your email address to receive a verification code.",
              ),
              const SizedBox(height: 16),
              TextField(
                controller: emailResetController,
                keyboardType: TextInputType.emailAddress,
                decoration: _buildInputDecoration(
                  'Email',
                  Icons.email_outlined,
                ),
              ),
              if (stepError.isNotEmpty)
                Text(stepError, style: const TextStyle(color: Colors.red)),
            ],
          ),
          actions: <Widget>[
            TextButton(
              onPressed: () => Navigator.of(context).pop(),
              child: const Text('Cancel'),
            ),
            ElevatedButton(onPressed: sendCode, child: const Text('Send Code')),
          ],
        );
      },
    );
  }

  // Step 2: Code Input
  void _showCodeInput(
    BuildContext context,
    String email,
    TextEditingController codeController,
    TextEditingController newPasswordController,
  ) {
    String stepError = '';

    void verifyCode() {
      if (codeController.text != '123456') {
        // Mock verification code
        // Update error locally (state outside widget is unnecessary here)
        ScaffoldMessenger.of(context).showSnackBar(
          const SnackBar(content: Text('Invalid code. Use 123456.')),
        );
      } else {
        Navigator.of(context).pop();
        _showNewPasswordInput(context, email, newPasswordController);
      }
    }

    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text("Reset Password (Step 2/3)"),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: <Widget>[
              Text(
                "A code has been sent to $email. Enter it below (Mock: 123456).",
              ),
              const SizedBox(height: 16),
              TextField(
                controller: codeController,
                keyboardType: TextInputType.number,
                decoration: _buildInputDecoration(
                  'Verification Code',
                  Icons.lock_open_outlined,
                ),
              ),
              if (stepError.isNotEmpty)
                Text(stepError, style: const TextStyle(color: Colors.red)),
            ],
          ),
          actions: <Widget>[
            TextButton(
              onPressed: () => Navigator.of(context).pop(),
              child: const Text('Cancel'),
            ),
            ElevatedButton(onPressed: verifyCode, child: const Text('Verify')),
          ],
        );
      },
    );
  }

  // Step 3: New Password Input
  void _showNewPasswordInput(
    BuildContext context,
    String email,
    TextEditingController newPasswordController,
  ) {
    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text("Reset Password (Step 3/3)"),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: <Widget>[
              const Text("Enter and confirm your new password."),
              const SizedBox(height: 16),
              TextField(
                controller: newPasswordController,
                obscureText: true,
                decoration: _buildInputDecoration(
                  'New Password',
                  Icons.vpn_key_outlined,
                ),
              ),
            ],
          ),
          actions: <Widget>[
            TextButton(
              onPressed: () => Navigator.of(context).pop(),
              child: const Text('Cancel'),
            ),
            ElevatedButton(
              onPressed: () {
                // Mock password update succeeded
                Navigator.of(context).pop();
                ScaffoldMessenger.of(context).showSnackBar(
                  const SnackBar(
                    content: Text('Password successfully updated!'),
                  ),
                );
              },
              child: const Text('Set New Password'),
            ),
          ],
        );
      },
    );
  }

  @override
  Widget build(BuildContext context) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.stretch,
      children: [
        // Email Field (Autofills last logged-in user's email)
        TextField(
          controller: _emailController,
          keyboardType: TextInputType.emailAddress,
          style: TextStyle(color: _calmPrimaryTextColor),
          decoration: _buildInputDecoration('Email', Icons.email_outlined),
        ),
        const SizedBox(height: 20),

        // Password Field with Visibility Toggle
        TextField(
          controller: _passwordController,
          obscureText: !_isPasswordVisible, // Toggled by state
          style: TextStyle(color: _calmPrimaryTextColor),
          decoration: _buildInputDecoration(
            'Password',
            Icons.lock_outline,
            suffixIcon: IconButton(
              icon: Icon(
                _isPasswordVisible ? Icons.visibility : Icons.visibility_off,
                color: _calmDarkBlue.withOpacity(0.6),
              ),
              onPressed: () {
                setState(() {
                  _isPasswordVisible = !_isPasswordVisible;
                });
              },
            ),
          ),
        ),
        const SizedBox(height: 10),

        // Forgot Password Link
        Align(
          alignment: Alignment.centerRight,
          child: TextButton(
            onPressed: () => _showForgotPasswordFlow(context),
            child: Text(
              'Forgot Password?',
              style: TextStyle(
                color: _calmDarkBlue,
                fontWeight: FontWeight.w600,
              ),
            ),
          ),
        ),
        const SizedBox(height: 30),

        // Error Display
        if (_error != null)
          Padding(
            padding: const EdgeInsets.only(bottom: 20.0),
            child: Text(
              _error!,
              textAlign: TextAlign.center,
              style: const TextStyle(
                color: Colors.redAccent,
                fontWeight: FontWeight.bold,
              ),
            ),
          ),

        // Login Button
        ElevatedButton(
          onPressed: _isLoading ? null : _login,
          style: ElevatedButton.styleFrom(
            backgroundColor: _calmAccentTeal, // Calm accent color
            padding: const EdgeInsets.symmetric(vertical: 18),
            shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.circular(10),
            ),
            elevation: 5,
          ),
          child: _isLoading
              ? const SizedBox(
                  width: 24,
                  height: 24,
                  child: CircularProgressIndicator(
                    color: Colors.white,
                    strokeWidth: 3,
                  ),
                )
              : const Text(
                  'LOGIN',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                    color: Colors.white,
                  ),
                ),
        ),
      ],
    );
  }
}

// ------------------------------------
// NEW: Sign Up Screen Component (with validation)
// ------------------------------------
class SignUpScreen extends StatefulWidget {
  const SignUpScreen({super.key});

  @override
  State<SignUpScreen> createState() => _SignUpScreenState();
}

class _SignUpScreenState extends State<SignUpScreen> {
  final TextEditingController _emailController = TextEditingController();
  final TextEditingController _passwordController = TextEditingController();

  // NEW: State for password visibility toggle
  bool _isPasswordVisible = false;

  bool _isLoading = false;
  String? _error;

  static const Color _calmAccentTeal = Color(0xFF5B9B9D);
  static const Color _calmDarkBlue = Color(0xFF4C7B8F);
  static const Color _calmPrimaryTextColor = Color(0xFF333D47);

  Future<void> _signUp() async {
    setState(() {
      _isLoading = true;
      _error = null;
    });

    final email = _emailController.text.trim();
    final password = _passwordController.text;

    // --- Validation Checks ---
    if (!email.endsWith('@gmail.com')) {
      return _showError('Email must end with @gmail.com only.');
    }
    if (password.length < 8) {
      return _showError('Password must be at least 8 characters.');
    }
    if (!password.contains(RegExp(r'[A-Z]'))) {
      return _showError('Password must contain at least one uppercase letter.');
    }
    if (!password.contains(RegExp(r'[0-9]'))) {
      return _showError('Password must contain at least one number.');
    }
    if (!password.contains(RegExp(r'[!@#$%^&*(),.?":{}|<>]'))) {
      return _showError(
        'Password must contain at least one special character.',
      );
    }
    // --- End Validation Checks ---

    try {
      await _authService.signUp(email, password);
    } catch (e) {
      _showError(e.toString());
    }
  }

  void _showError(String message) {
    if (mounted) {
      setState(() {
        _error = message;
        _isLoading = false;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.stretch,
      children: [
        // Email Field
        TextField(
          controller: _emailController,
          keyboardType: TextInputType.emailAddress,
          style: TextStyle(color: _calmPrimaryTextColor),
          decoration: _buildInputDecoration(
            'Email (Must end with @gmail.com)',
            Icons.email_outlined,
          ),
        ),
        const SizedBox(height: 20),

        // Password Field with Visibility Toggle
        TextField(
          controller: _passwordController,
          obscureText: !_isPasswordVisible, // Toggled by state
          style: TextStyle(color: _calmPrimaryTextColor),
          decoration: _buildInputDecoration(
            'Password (Min 8, U/C, #, Symbol)',
            Icons.lock_outline,
            suffixIcon: IconButton(
              icon: Icon(
                _isPasswordVisible ? Icons.visibility : Icons.visibility_off,
                color: _calmDarkBlue.withOpacity(0.6),
              ),
              onPressed: () {
                setState(() {
                  _isPasswordVisible = !_isPasswordVisible;
                });
              },
            ),
          ),
        ),
        const SizedBox(height: 10),

        // Validation Hint
        const Padding(
          padding: EdgeInsets.only(bottom: 20.0),
          child: Text(
            'Password requires 8+ chars, Uppercase, Number, and Symbol.',
            style: TextStyle(color: Colors.white70, fontSize: 12),
          ),
        ),

        // Error Display
        if (_error != null)
          Padding(
            padding: const EdgeInsets.only(bottom: 20.0),
            child: Text(
              _error!,
              textAlign: TextAlign.center,
              style: const TextStyle(
                color: Colors.redAccent,
                fontWeight: FontWeight.bold,
              ),
            ),
          ),

        // Sign Up Button
        ElevatedButton(
          onPressed: _isLoading ? null : _signUp,
          style: ElevatedButton.styleFrom(
            backgroundColor: _calmAccentTeal, // Calm accent color
            padding: const EdgeInsets.symmetric(vertical: 18),
            shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.circular(10),
            ),
            elevation: 5,
          ),
          child: _isLoading
              ? const SizedBox(
                  width: 24,
                  height: 24,
                  child: CircularProgressIndicator(
                    color: Colors.white,
                    strokeWidth: 3,
                  ),
                )
              : const Text(
                  'SIGN UP',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                    color: Colors.white,
                  ),
                ),
        ),
      ],
    );
  }
}

// Helper for consistent input styling (Used by Login, Sign Up, Forgot Password)
InputDecoration _buildInputDecoration(
  String label,
  IconData icon, {
  Widget? suffixIcon,
}) {
  const Color _calmAccentTeal = Color(0xFF5B9B9D);
  const Color _calmDarkBlue = Color(0xFF4C7B8F);
  const Color _calmPrimaryTextColor = Color(0xFF333D47);

  return InputDecoration(
    labelText: label,
    labelStyle: TextStyle(color: _calmPrimaryTextColor.withOpacity(0.7)),
    filled: true,
    fillColor: Colors.white.withOpacity(0.85),
    prefixIcon: Icon(icon, color: _calmDarkBlue),
    suffixIcon: suffixIcon, // Added optional suffix icon support
    contentPadding: const EdgeInsets.symmetric(vertical: 18, horizontal: 15),
    border: OutlineInputBorder(
      borderRadius: BorderRadius.circular(10),
      borderSide: BorderSide(color: Colors.white.withOpacity(0.2), width: 1),
    ),
    enabledBorder: OutlineInputBorder(
      borderRadius: BorderRadius.circular(10),
      borderSide: BorderSide(color: Colors.white.withOpacity(0.4), width: 1),
    ),
    focusedBorder: OutlineInputBorder(
      borderRadius: BorderRadius.circular(10),
      borderSide: const BorderSide(color: _calmAccentTeal, width: 2),
    ),
  );
}

